mapper.select:
WITH AMG AS (
SELECT A.*, M.name AS writerName, IFNULL(G.groupName,'전체') AS groupName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id LEFT JOIN `group` AS G ON A.classId = G.id WHERE boardId = #{boardId}
ORDER BY A.id DESC;
) SELECT AMG.*, IF(COUNT(H.hwMsg) = 0, '미검사','검사') AS 'hwChk' FROM AMG LEFT JOIN `homework` AS H ON AMG.id = H.relId GROUP BY AMG.id ORDER BY AMG.id DESC;
) SELECT AMG.*, F.id AS fileId FROM AMG LEFT JOIN `file` AS F ON AMG.id = F.relId GROUP BY AMG.id ORDER BY AMG.id DESC;

mapper.update:
UPDATE article
SET updateDate = NOW(),
title = #{title},
`body` = #{body},
youTubeLink = #{youTubeLink}
WHERE id = #{id}

UPDATE article
SET regDate = #{regDate},
title = #{title},
classId = #{classId}
WHERE id = #{id}

UPDATE article
SET youTubeLink = #{youTubeLink}
WHERE id = #{id}

UPDATE article
SET hit = hit + 1
WHERE id = #{id}

mapper.select:
SELECT *
FROM article
WHERE memberId = #{loginedMemberId}
AND boardId = #{boardId}
AND regDate LIKE CONCAT('%',#{today},'%');
