mapper.select:
SELECT M.*, G.groupName
FROM `member` AS M
INNER JOIN `group` AS G
ON M.classId = G.id
WHERE classId = #{classId}
AND authLevel = 2;

mapper.update:
UPDATE `member`
SET name = #{name},
cellphoneNum = #{cellphoneNum},
email = #{email}
WHERE id = #{id}

mapper.select:
SELECT *
FROM `member`
WHERE name = #{name}
AND email = #{email}

mapper.update:
UPDATE `member`
SET classId = #{classId}
WHERE id = #{memberId}
OR babyId = #{memberId}
